import React, { useState, useEffect } from "react";
import axios from "axios";

// Change these to your test user IDs
const USER_ID = "6866a789ed224bc42820cbc9"; // Example: Email Timaru
const OTHER_USER_ID = "6866a8a0cca934b2567caac1"; // Example: Silviu Timaru

const API_BASE = "http://localhost:3000";

function App() {
  const [friends, setFriends] = useState([]);
  const [pendingInvitations, setPendingInvitations] = useState([]);
  const [privateRooms, setPrivateRooms] = useState([]);
  const [inviteId, setInviteId] = useState("");
  const [roomName, setRoomName] = useState("");
  const [roomMembers, setRoomMembers] = useState([USER_ID, OTHER_USER_ID]);
  const [message, setMessage] = useState("");

  // Fetch friends, invitations, and rooms on load
  useEffect(() => {
    fetchFriends();
    fetchRooms();
  }, []);

  const fetchFriends = async () => {
  try {
    const res = await axios.get(`${API_BASE}/friends/list/${USER_ID}`);
    setFriends(res.data.friends);
    setPendingInvitations(res.data.pendingInvitations);
    setMessage(""); // Clear any previous error message
  } catch (err) {
    setMessage("Error fetching friends");
  }
};

  const fetchRooms = async () => {
  try {
    const res = await axios.get(`${API_BASE}/friends/rooms/${USER_ID}`);
    setPrivateRooms(res.data.privateRooms);
    setMessage(""); // Clear any previous error message
  } catch (err) {
    setMessage("Error fetching rooms");
  }
};

  const sendInvitation = async () => {
    if (!inviteId) return;
    try {
      await axios.post(`${API_BASE}/friends/invite`, {
        fromUserId: USER_ID,
        toUserId: inviteId,
      });
      setMessage("Invitation sent!");
      setInviteId("");
      fetchFriends();
    } catch (err) {
      setMessage("Error sending invitation");
    }
  };

  const acceptInvitation = async (fromUserId) => {
    try {
      await axios.post(`${API_BASE}/friends/accept`, {
        userId: USER_ID,
        fromUserId,
      });
      setMessage("Invitation accepted!");
      fetchFriends();
    } catch (err) {
      setMessage("Error accepting invitation");
    }
  };

  const createRoom = async () => {
    if (!roomName || roomMembers.length < 2) {
      setMessage("Room name and at least 2 members required");
      return;
    }
    try {
      await axios.post(`${API_BASE}/friends/room`, {
        name: roomName,
        memberIds: roomMembers,
      });
      setMessage("Room created!");
      setRoomName("");
      fetchRooms();
    } catch (err) {
      setMessage("Error creating room");
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "40px auto", fontFamily: "Arial, sans-serif" }}>
      <h2>Friends & Private Rooms</h2>
      {message && <div style={{ color: "green", marginBottom: 10 }}>{message}</div>}

      <section style={{ marginBottom: 30 }}>
        <h3>Your Friends</h3>
        <ul>
          {friends.map((f) => (
            <li key={f._id}>
              {f.firstName} {f.lastName} ({f.email})
            </li>
          ))}
        </ul>
      </section>

      <section style={{ marginBottom: 30 }}>
        <h3>Pending Invitations</h3>
        <ul>
          {pendingInvitations.map((inv) => (
            <li key={inv._id}>
              {inv.firstName} {inv.lastName} ({inv.email}){" "}
              <button onClick={() => acceptInvitation(inv._id)}>Accept</button>
            </li>
          ))}
        </ul>
      </section>

      <section style={{ marginBottom: 30 }}>
        <h3>Send Friend Invitation</h3>
        <input
          type="text"
          placeholder="Enter user ID to invite"
          value={inviteId}
          onChange={(e) => setInviteId(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <button onClick={sendInvitation}>Send Invitation</button>
      </section>

      <section style={{ marginBottom: 30 }}>
        <h3>Your Private Rooms</h3>
        <ul>
          {privateRooms.map((room) => (
            <li key={room._id}>
              <b>{room.name}</b> (Members:{" "}
              {room.members.map((m) => `${m.firstName} ${m.lastName}`).join(", ")})
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h3>Create Private Room</h3>
        <input
          type="text"
          placeholder="Room name"
          value={roomName}
          onChange={(e) => setRoomName(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <button onClick={createRoom}>Create Room</button>
        <div style={{ fontSize: 12, color: "#888", marginTop: 4 }}>
          (Room will include you and the other test user)
        </div>
      </section>
    </div>
  );
}

export default App;
